  `remed` int(1) NOT NULL DEFAULT '1' COMMENT '1:推荐0：不推荐',
  `addtime` int(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='山西景点管理' AUTO_INCREMENT=13 ;

