--
-- 表的结构 `qc_book`
--

CREATE TABLE IF NOT EXISTS `qc_book` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `type` varchar(70) NOT NULL COMMENT '订单类型',
  `booknum` varchar(100) NOT NULL COMMENT '订单号码',
  `name` varchar(70) NOT NULL COMMENT '订单名称（商品名称）',
  `note` text NOT NULL COMMENT '备注',
  `contact` varchar(50) NOT NULL COMMENT '联系人',
  `tel` text NOT NULL COMMENT '联系电话',
  `action` varchar(200) NOT NULL COMMENT '收货地址',
  `number` varchar(30) NOT NULL COMMENT '购买数量',
  `price` float NOT NULL DEFAULT '0.01' COMMENT '商品单价',
  `allprice` float NOT NULL DEFAULT '0' COMMENT '总价',
  `buystatus` int(11) NOT NULL COMMENT '付款状态（1：付款，0：未付款）',
  `bookstatus` int(11) NOT NULL DEFAULT '0' COMMENT '订单状态（1：已发货，0：未发货）',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '1：修改，0:没有修改',
  `addtime` int(11) NOT NULL COMMENT '下单时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=42 ;

