--
-- 表的结构 `qc_buddhist`
--

CREATE TABLE IF NOT EXISTS `qc_buddhist` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '编号',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `about` varchar(255) NOT NULL COMMENT '佛事简介',
  `max` varchar(70) NOT NULL,
  `medium` varchar(70) NOT NULL,
  `mini` varchar(70) NOT NULL,
  `remed` int(1) NOT NULL DEFAULT '1',
  `addtime` int(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='佛事活动' AUTO_INCREMENT=12 ;

--
-- 转存表中的数据 `qc_buddhist`
--

INSERT INTO `qc_buddhist` (`id`, `title`, `content`, `about`, `max`, `medium`, `mini`, `remed`, `addtime`) VALUES
(2, '释迦牟尼佛的故事', '&lt;p&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;兰毗尼花园(今尼泊尔境内)。释迦牟尼的诞生地。离此地西边14公里就是释迦族的古都迦毗罗卫城(现已&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;不存任何建筑物。近年考古证明该地为迦毗罗卫城的遗址)。图上的白色建筑物是为纪念释迦牟尼的母亲摩&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;耶王后而建的“摩耶堂”。堂右侧枝叶茂盛的无忧树，传说摩耶王后就是抚该树枝动了胎气生下了释迦牟尼。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;堂前的水池，传说摩耶王后产前和生下的太子都曾在池中沐浴。&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;　　二千五百多年前，在古印度的西北部，喜玛拉雅山脚下，有个叫迦毗罗卫(今尼泊尔境内)的王国。这里&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;山上生长着茂密的森林，平原盛产稻米，草原适合畜牧业，是个美丽富饶的地方。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;国王姓乔达摩，名字叫首图驮那。这名字的意思是纯净的稻米，所以称他为净饭王，属于释迦族。王后叫摩&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;诃摩耶，是邻国天臂城善觉王的长女。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王与摩耶王后之间的感情非常好，但结婚多年都没有生育儿女。净饭王为没有王位继承人而十分苦恼。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;直到净饭王年已50岁，摩耶王后45岁的时候才第一次怀孕。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;据佛教传说，摩耶王后这次怀孕，是由于睡眠时梦见一头六牙白色大象腾空而来，从右肋进入王后的腹中。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;王后自从怀孕后，心情非常愉快，再没有忧虑与烦恼，从此也再没有发过怒气，断绝了贪欲和虚伪心情，每&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;天只是喜欢到幽静树林和水溪旁散步。 按当时印度的风俗，妻子回娘家分娩，丈夫不可同行。图中只有象&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;轿上坐着的摩耶王后，没有净饭王。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;按当时古印度的风俗，妇女头胎分娩，必须回到娘家去。摩耶王后怀孕期满，净饭王为摩耶王后备了由两头&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;大象载的轿子，并派了许多宫女、侍臣，护送摩耶王后回娘家天臂城去。 图中抱着小太子的是传说中的天&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;王，天王身后是诸梵神。寓意上天将释迦牟尼赐给人间。手扶树枝站立的是摩耶王后。佛经上说：太子是从&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;摩耶王后的右肋出生的。摩耶王后夜梦中的白象也是从右肋进入体内而怀孕的。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;摩耶王后在回娘家天臂城分娩的途中，经过迦毗罗卫城和天臂交界处的兰毗尼花园时，感到有点旅途疲乏，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;就下轿到花园中休息。当摩耶王后走到一棵葱茏茂盛的无忧树下，伸手去抚树枝时，惊动了胎气，就在树下&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;生下了太子。&lt;br/&gt;　　太子诞生的时间是公元前565年，中国农历的四月八日。佛教将这一天定为“佛诞节”，也称“浴佛节”。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;图中左侧跪在太子身边的是闻太子诞生而赶到兰毗尼花园的净饭王(绿衣者)，单腿跪在净饭王身边的是净饭&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;王的大弟弟白饭王(红衣者)站在净饭王身后举手欢呼的是其二弟甘露王。站在画面中间的是摩耶王后，两侧&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;空中是为太子祝福的梵神。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说：太子降生的时候，天空仙乐鸣奏、花雨缤纷，诸天神拱卫。一时间宇宙大放光明，万物欣欣向&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;荣。天空直泻下两条银链似的净水，一条温暖，一条清凉，来为太子沐浴(这也是佛教定为浴佛节的典故)。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;太子刚生下来就能自己行走7步。太子每走一步，他的脚下就涌现出一朵莲花。并且太子右手指天，左手指&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;地，大声宣称：“天上天下，唯我独尊。”&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛教徒在“佛诞节”举行“浴佛法会”时，就是要在大雄宝殿上用净水香汤沐浴太子像，这太子像是数寸高的，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;右手指天，左手指地的童子像。但是中国汉地佛教徒供奉的太子像是左手指天，右手指地。这是因为中国古&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;代崇尚左，以左为尊。其实这是不符合佛经的。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说：摩耶王后生下太子之后，迦毗罗卫全国不断出现吉祥的事情。浑浊的江河水变得清澈了，五谷丰&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;登，花木繁茂，人与人之间也变得和睦了。与太子同日降生的孩子，母子都十分健康。产生的牛、马也都十&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;分健壮，连一根杂色鬃毛都没有。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王听到摩耶王后在兰毗尼花园生下了太子，高兴万分，立即带领众多的宫女侍臣，车马和特制的华丽的&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;大轿，赶到兰毗尼花园，将王后和太子接回皇宫。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;太子诞生后的第5天，净饭王请来许多全国有名望的学者来为太子取名字。经过几番讨论，大家一致同意太&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;子应取名叫乔达摩·悉达多。悉达多的意思是“吉祥”和“成就一切”。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;摩耶王后生下乔达摩·悉达多太子7天之后就因病去世了。幼年时代悉达多太子就由姨母摩诃波阁波提王妃抚&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;养。波阁波提王妃是在姐姐摩耶王后去世后续嫁给净饭王的。波阁波提王妃容貌端庄秀丽，性格温柔、娴&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;静。她对悉达多小外甥精心抚育，十分钟爱。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说：悉达多太子出生不久，一位很有名望的预言家,号称阿私陀的仙人来到王宫给太子占相。阿私陀&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;看到太子，先是非常惊喜，转而又悲伤地流下了泪水。净饭王问他为何又喜又悲?阿私陀仙人回答：太子的&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;相貌太好了，人间找不出第二人来!将来如果继承王位，一定是位“转轮王”(印度古代称能以威望统一四方天&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;下的君主)，因此我为国王高兴。但据我的观察，太子必定要出家学道，并能得到最高成就，成为人天至尊&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的导师，拯救世人离苦海。可惜我已经老了，听不到太子的教诲了，所以我为自己悲哀。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王听到占相仙人的话，也是又喜又忧。他当然希望自己的儿子成为统一天下的转轮王，国王从此千方百&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;计防止太子出家修道。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子在幼年的时候，就有沉思的习惯，世间许多现象被他看到之后，都容易引起他的感触和深思。在&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;传统的“王耕节”时国王要在这一天亲自耕种土地，净饭王带领悉达多太子来到田野，太子看见在田地里的农&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;夫赤背裸身在烈日下吃力地劳作，耕田的牛被绳索鞭打皮破血流，被犁铧翻出来的小虫蚯蚓，被鸟雀竞相啄&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;食，鸟雀又被蛇、鹰吞食。这一幅幅生存斗争，弱肉强食的情景，使王子感到很痛苦，他无心游玩，就走到&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;一棵阎浮树下静坐沉思。&lt;br/&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说：悉达多太子坐在树下沉思，有时能自然升空许久，净饭王和太子的姨母波阁波提王妃十分惊奇，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;不由得就朝太子礼拜。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;乔达摩·悉达多太子越长相貌越奇伟，天资十分聪慧。他的父亲净饭王对他期望很大，希望他继承王位后，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;建功立业，成为一个转轮王。从太子7岁开始学习当时王族应具备的一切学问和技艺。太子学到12岁，就已&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;经掌握了印度当时最高的学术，即五明和四吠陀。五明即是：①语文学的声明。②工艺学的工巧明。③医药&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;学的医方明。④论理学的因明。⑤宗教学的内明。四吠陀即是：①养生之法的梨俱吠陀。②祭祀祝词的搓马&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;吠陀。③兵法研究的夜柔吠陀。④咒求文献的阿闼吠陀。四吠陀是当时婆罗门教的经典，被奉为至高无上，贵族必读。&lt;br/&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;　　悉达多太子又向武士们学习武术，练成了一个骑马、射箭、击剑的能手。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王举行一次武艺竞赛大会，释迦族的王子们都参加比赛。悉达多太子骑射出众，夺得冠军，万人敬仰。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王发觉自己的儿子对人世间的苦难情景思虑太重，深怕太子产生厌世出家念头，就为他建造了三座豪华&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的冬天防寒，夏天避暑，雨季防潮的宫殿(称三时殿)，又选来上百名美丽的少女随时为太子歌舞。在悉达多&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;太子16岁的时候，便为他娶了他的表妹耶输陀罗公主为妃子。企图从生活的享乐上束缚太子，让他放纵情&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;欲，沉迷娱乐。但是这一切并未能引起悉达多太子的兴趣，反而使他感到太喧嚣，太骚扰人，使他很厌烦。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;更引起他深远的思考：“人世间有数不尽的苦痛和忧虑都未能解决，一味的追求享乐就能解脱吗?人的生命是&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;短暂的，享乐又能到几时呢?”因此，悉达多太子经常躲避开喧哗的歌舞，独自到幽静的树林和河边去散步思考。&lt;br/&gt;　　世间凡是做父亲的人，不管是国王还是平民百姓，都是怕自己的儿子贪图享乐，放纵情欲。可是，净饭&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;王却怕太子清心寡言，产生厌世，出家的念头，因此，除了为太子修建豪华的宫殿，为太子娶了美丽的妻子&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;外，还时刻关心太子情绪变化，一见到太子沉默寡言，闷闷不乐时，就要设法让太子高兴起来。当净饭王发&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;现太子对宫里的歌舞感到喧骚烦嚣时，就让太子坐马车去游玩，并告诉驾车的驭者，一定要带太子到有欢乐&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的人群和有美丽的景色的地方去。可是，悉达多太子喜欢幽静的环境，他叫给他驾车的名叫车匿的驭者，把&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;他带到城外去。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子乘马车出城。他刚来到京城的东门，看见一个须发全白，弯腰驼背，行走艰难还不断呻吟的老&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;人。悉达多看到这个情景就想到：衰老是人人都逃避不了的命运啊!人在儿童时受父母宠爱，青年时英俊萧&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;洒，壮年时精力充沛，但是到了晚年，人就要在许多痛苦中度日了。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多看到老人后心情难过，就叫车匿调转马车，驱车向京城南门走来。刚到南门，又见到一个满身生疮流&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;血的病人。病人一边走，一边不断痛苦地呻吟着。悉达多太子想到：疾病也是每个人都不能避免的。人的一&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;生要害多少次疾病，要遭受多少痛苦，真是难以想象的啊!&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;接着，悉达多太子又调转车来到京城的西门，看见两个人抬着一个死人迎面走来，随行的亲属悲痛地嚎哭&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;着。悉达多太子叹息地自语：死亡是可怕的，每个人又都难免一死，当死亡来临时给每人带来的恐惧，使人&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;多么难以忍受啊!自己的死亡又造成许多亲朋的无限悲痛。这些痛苦，永无休止，任何人都摆脱不了，这叫&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;多么苦闷，多么烦恼啊!他立即又离开西门。&lt;br/&gt;　　最后，悉达多太子驱车来到了京城的北门，看见一个出家修道的人(印度当时称出家人为“沙门”)从对面&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;走来。这沙门身穿袒右肩的黄色法衣，一手持法杖，一手托钵，态度严肃安详。悉达多太子想道：这出家人&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;是一定不会有俗人那么多的痛苦和烦恼的。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子美丽的王妃耶输陀罗，为太子生下了一个儿子。净饭国王十分高兴，命全国举行庆贺。但悉达多&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;太子的内心却感到沉重。他给自己的儿子所取的名字叫罗侯罗。这名字的意思是“覆障”，就是潜伏的一种障&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;碍。这时的悉达多太子已经有了出家修行的念头，他怕因为有了儿子而增加他留恋家庭的感情。“罗侯罗”这&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;个名字的意思也有解释为“圈子”，就是说有了儿子要受到束缚，就像被圈子套住了一样。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;终于，在悉达多太子29岁(也有说19岁)时的某一天，他郑重地恳求父亲净饭国王允许他出家修行。净饭王听&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;到儿子要出家，如同霹雳轰顶，他流着泪要求悉达多打消出家的念头，将来好继承他的王位。悉达多就向父&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;王提出：如能满足他的四个要求，他就不出家。一是没有衰老的现象，二是没有疾病的痛苦，三是没有死亡&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的恐怖，四是所有的东西不损不灭。&lt;br/&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王看到儿子出家的决心已坚定，没有能力说服儿子，只好多派人看护悉达多太子，防止他离开皇宫。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;净饭王命令宫女时刻不离太子，演奏着美妙的音乐，跳着优美的舞蹈，竭尽媚力讨太子欢心。有一天半夜&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;里，悉达多太子醒来，他见到歌舞一天的宫女们都疲倦地睡在周围，每个人都是披头散发，脂粉残脱，袒胸&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;露胯。有的在说着梦话，流着涕涎，有的发出鼾声，姿态丑陋，平时妖艳姿容荡然不见了。太子不由心中一&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;惊，感叹道：这世界上有许多事物都是虚假的啊!我要解脱掉这一切，我不可再踌躇、再犹豫了。太子现在&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的心情，就如同这整个宫殿燃起了熊熊大火，他必须立刻逃出去一样。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;在二月初八日(中国农历)的午夜，悉达多太子起身先来到妻儿的卧室，他凝视着熟睡中的耶输陀罗公主和儿&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;子罗侯罗默默地向他们告别后，毅然转身离开了父亲为他修建的豪华的宫殿。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子又来到马厩，唤醒了名叫车匿的马夫，叫他牵出一匹名叫犍陡的白色骏马，备上马鞍，太子跨上&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;马背，由车匿伴随，在整个皇宫里的人都在梦乡的时候，太子跃马出了京城的北门，在白茫茫的月色中，朝&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;山林奔驰而去。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;拂晓时分，悉达多太子已来到了离京城百多里路的阿拨弥河边罗摩村山林中，他拔剑斩断了自己的头发，改&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;扮成出家人的模样，然后命车匿回王宫去告诉父亲净饭王说自己已经出家修行去了。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子毅然决然地舍弃王位出家修行，究其原因，有社会方面的影响，也有他个人的认识。悉达多太子&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;所处的时代正是古印度各国之间互相讨伐、并吞，阶级和民族矛盾十分尖锐。他所属的释迦族是一个较为弱&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;小的民族，受到邻国强权的威胁，朝不保夕，时有被灭亡的危险。因而认为世间一切事物和概念都在生与灭&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;中变化着，没有永恒的幸福，而种种痛苦却是无休止的。另外，他又目睹人自有生以后，接踵而来的老、&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;病、死的情景，联想到自己也摆脱不了同样的命运，从而产生了人生难脱苦难的烦恼。他为了在精神上获得&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;解脱之道，终于出家修行。&lt;br/&gt;&lt;br/&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt; &amp;nbsp; &amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;　　悉达多太子出家后，先到了跋伽仙人的苦行林，那里的修行者有的披着草衣，有的身着树皮，或躺在泥土里，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;或卧在荆棘上，以种种苦行折磨肉体，以求得精神的解脱。悉达多不满意这种做法，滞留一宿便离去了。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多的父亲净饭王听到儿子出家的消息，十分悲伤，经派人劝说无效，便在亲族中选派了阿若鞒侨陈如、阿&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;说示、跋提、十力迦叶、摩诃男拘利等5人伴随他。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多带着5个随从渡过恒河，到了摩揭陀国的首都王舍城，国王频婆娑罗会见了他，并请他应允，如果得&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;道，请先来度我频婆娑罗。尔后，悉达多寻访隐栖在王舍城附近山林的数论派信奉者阿罗逻·迦罗摩和郁罗迦·&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;罗摩子，跟他们学习禅定。然而他们的教义仍然不是真正的人生解脱之道，便离开了他们。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多带领五个随从又来到尼连禅河边的加阁山苦行林中，和那里的苦行人一起实行极端刻苦的修行。悉达多&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;为了寻求解脱，他静坐思维，身不着衣，不避风雨，每日仅食一麦或一麻，坚持不懈达六年之久，身体已极度&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;消瘦，但仍没有找到真正解脱的方法。于是他悟到：当时印度的哲学思想中没有真正能使人达到大彻大悟的道&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;理，自己只是一味的苦行是徒劳无益的，于是他决定结束苦行。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多结束苦行后，先到尼连禅河中洗去了他身上6年的积垢，随后接受了河边牧羊女供养的牛奶，恢复了体&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;力。随从他的5个人见他这样做，都以为他放弃了信心和努力，便离开了他，前往波罗奈城的鹿野苑去继续他&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;们的苦行。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说，悉达多太子站在尼连河边，手捧铁制的钵多罗(简称钵，出家人乞食盛饭器)，默念道：我将此钵投&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;入河中，此钵如能浮出水面并逆水漂行，我就必能在此地彻底觉悟，得到解脱。当悉达多将钵投入水中后，果&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;然铁钵浮出水面，逆水漂行。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子独自一人走到一棵枝叶繁茂的毕钵罗树下?拾了一些草叶铺了一个座位，他面向东方，盘腿静坐，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;当时发下誓愿：“我若不能证到无上大觉，宁让此身粉碎，终不起此座!”&lt;br/&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;他静思冥索，总结过去修行的经历，重新调整思维方法，深究宇宙间一切现象的规律和人生解脱之道。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说，魔王害怕悉达多太子真正觉悟，就派了三名魔女(这三个魔女是：爱欲、乐欲和贪欲)来殷勤献媚，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;诱惑太子。但太子对魔女淫荡的挑逗视而不见，毫不动心。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;魔王见魔女引诱没有成功，就带领众魔鬼亲自来到悉达多太子座前。魔王威胁太子说：如果太子不立即回到皇&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;宫去享受荣华富贵的生活，就让太子粉身碎骨死在树下。悉达多太子专心修行思考，对魔王的威胁就如同没有&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;听见。魔王命众鬼刀箭齐发，杀向太子。但魔鬼的刀箭却不能挨近太子的身体。这时天空一声巨响，护法天神&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;来帮助太子，将魔鬼全部驱散。&lt;br/&gt;　　终于，在十二月初八日(中国农历)的凌晨，悉达多太子战胜了最后的烦恼，获得了彻底的觉悟而成了大智慧的佛陀。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子成佛的年龄是35岁(也有说是30岁)。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多太子所觉悟到的真理，就是：①四谛；②八正道；③缘起论；④三法印。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛陀的意思是“觉者”或“智者”，就是我们平时简称的佛。佛教对佛的解释有三种涵义：①正觉：就是对宇宙间&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;一切事物无增无减地、如实地了解了，觉察了；②等觉或遍觉：就是不仅自己觉悟了，而且能平等普遍的使别&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;人也觉悟；③圆觉或无上觉：就是自己觉悟和使别人觉悟的智慧和行动、功德都达到了最高和最圆满的境地。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;悉达多达到了这种觉悟的境地，所以他成佛了。因为他属于释迦族，人们就尊称他为释迦牟尼，意思是释迦族的圣人。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;因为悉达多是坐在毕钵罗树下成佛的，毕钵罗树从此就都叫做菩提树了。菩提就是“觉”的意思。悉达多成佛的&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;地方，称为菩提伽耶，又称菩提道场和佛陀伽耶。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;释迦牟尼在菩提树下大悟成佛后，无比的喜悦，佛陀的这种得到彻底解脱的愉快，是不可能用几句话形容得了&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的。佛陀从座位上站起来，他在附近的树下踱步，反复品味着自己所觉悟到的真理，一连在树下呆了3个7日&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;(21天)，因为这种获得真理，得到解脱的快乐，使佛陀忘记了时间，忘记了自己，他只是感到他的思维在扩&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;大，在升腾，已经和宇宙融为一体了。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;佛经上说：释迦牟尼成佛后，并不想把自己悟到的真理传授给他人。因为佛陀想到，世上一切众生都是在追求&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;名利和争取自己满足欲望，我静悟到的真理，世人未必愿意接受，或许还要遭到世人的诽谤和讥嘲。只要我解&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;脱，永不烦恼，与世无争就算了。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;但是，天神劝请佛陀慈悲为怀，救济众生，脱离苦海。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;释迦牟尼成佛后，就以大慈悲的心情，博大精深的智慧，不畏艰苦的精神，开始了40年不间断的弘扬佛法，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;教化众生的活动。他最初说法，是到波罗奈城的鹿野苑，化度随侍过他的5个寺者阿若侨陈如等人。佛陀在鹿&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;野花苑的第一次说法，在佛教称为“初转法轮”。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;讲说佛法为什么叫做转法轮呢?“轮”是印度古代战争中用的一种武器，它的形状像个轮子。印度古代有一种传&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;说，征服四方的大王叫做转轮王，转轮王出世时，空中就出现此轮，预示他前途无敌，这里用轮来比喻佛所说&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;的法。佛的法轮出现在世上，一切不正确的见解，不善的法都将破碎无余，所以把说佛法叫做转法轮。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;释迦牟尼当初出家的目的，是为了寻求解脱生老病死等痛苦之道。他在鹿野苑第一次说法时，以浅显的语言，&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;生动的比喻，讲述如何修道才能解脱烦恼永离苦海的真理。他所讲的主要内容有以下几个方面：①四谛；②八&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;正道；③缘起论；④三法印。这些是佛教的根本教义，是释迦牟尼证悟以后所形成的自己独特的观察和分析事&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;物的观念。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimSun; font-size: 14px;&quot;&gt;四谛，也称四圣谛。“谛”是实在和真理的意思。四谛即苦、集、灭、道。&lt;br/&gt;　　&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 5px; text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 宋体, SimS', '兰毗尼花园(今尼泊尔境内)。释迦牟尼的诞生地。离此地西边14公里就是释迦族的古都迦毗罗卫城(现已不存任何建筑物。近年考古证明该地为迦毗罗卫城的遗址)。图上的白色建筑物是为纪念释迦牟尼的母亲摩耶王后而建的“摩耶堂”。堂右侧枝叶茂盛的无忧树，传说摩耶王后就是抚该树枝动了胎气生下了释迦牟尼。堂前的水池，传说摩耶王后产前和生下的太子都曾在池中沐浴。\r\n\r\n', '2013_12/max_52a074a49e5ea.jpg', '2013_12/medium_52a074a49e5ea.jpg', '2013_12/mini_52a074a49e5ea.jpg', 1, 1388071364);
INSERT INTO `qc_buddhist` (`id`, `title`, `content`, `about`, `max`, `medium`, `mini`, `remed`, `addtime`) VALUES
(3, '在五台山如何烧头香', '&lt;p&gt;1 何谓烧头香烧头香分为绝对和相对的头香。相对头香是说，到五台山信众和游客，第一件事情往往是到寺院敬香，称之为烧头香；礼佛期间清晨只要您早早前去寺庙进香，也称之为头香。绝对头香就是大年初一全山燃起的...&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;1 何谓烧头香&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;烧头香分为绝对和相对的头香。相对头香是说，到五台山信众和游客，第一件事情往往是到寺院敬香，称之为烧头香；礼佛期间清晨只要您早早前去寺庙进香，也称之为头香。绝对头香就是大年初一全山燃起的第一柱香即为五台山烧头香。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;2 烧头香的历史由来&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;烧香之俗，在中国古已有之。大致说来，烧香可分为祭祀时所烧之香和日常生活之烧香。祭祀烧香，祭拜神灵;而日常生活烧香则主要是除臭作用。古时用香料来熏染房屋、衣物是人们平常生活的一个方面，甚至还成了达官贵人争奢斗富的一个手段。在唐代，长安富商王元宝在床前置木雕矮童二人，捧七宝博山炉，彻夜焚香。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;在中国，尤其是佛教圣地之首的五台山，信众游客不远万里、漂洋过海就是为了给佛敬一柱香，有的香客风尘仆仆，经过几天的奔波劳顿，到达圣地五台山第一件事就是进香，然后就匆匆而去。人们敬神拜佛要烧香，而世界上其它国家同样也会在神灵前烧香礼敬。作为佛教发源地的印度烧香，也源于早期信仰活动。同时由于印度环境因素的关系，还形成了他们当地特有的烧香习俗。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;3 烧头香的意义&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;在佛家看来，烧香礼佛的真实意义和目的在于表达对佛陀的尊敬、感激与怀念。去染成净，奉献人生，觉悟人生，如此而行，自然福慧具足，心想事成。同时烧香在烧香人看来，可以以此与神灵心灵相通，将你自己的心愿传达给佛祖，使自己心想事成。因此，在佛家又有“香为佛使”之说:香能通达人之信心于佛，犹如佛之使者，故称为佛使。《诸经要集》卷第五:“若有供者，手执香炉而唱时至。佛言:香为佛使。故须烧香遍请十方。”&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;烧头香不仅表现一个信徒的虔诚，还可以将自己的心思第一个传达给神灵，使神灵能够感应，实现自己的愿望。《异闻总录》里韩元英因未烧到头香而卒，《看钱奴》中周荣祖夫妇烧头香而得到了儿子的消息，并找回了属于自己家的财宝，都说明了这个问题。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;烧头香与佛教有着接不开的渊源。五台山的佛教寺院元规模为世界之首，成为世界五大佛教圣地之一，也是中国四大佛教名山之首，因此其烧香的更具有非凡的意义。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4 如何烧头香？&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.1 首先为了表达虔诚之心，我们要更换新衣、沐浴洗澡，做到周身一尘不染，着装整洁。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.2 敬香要空腹前往寺院，不可吃过饭再去进香。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.3 烧头香的前1日，要素食。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.4 如何进入寺院的门？选门进，寺庙建筑都有许多道门，从哪一道门进入都有讲究的。据说只有开了光的寺庙才有资格修十三道门。正中间的三道门，才是供人出入的。普通游客，进门只能走右边的那道门，中间那道门叫空门，只有出家人才可以出入的。进门时，女客先迈右脚，男客先迈左脚，都要注意，一定不能踩在门槛上，而且这步子，需迈得越大越好。（但后来听寺庙的和尚说，根本没有这回事。佛怎么会跟前来上香的善男信女计较那么多呢。）&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.5 烧几株香？烧香的话，三炷为自己祈福，六炷为两辈人祈福，九炷为三代人祈福。而十三是一个极致，十三炷香就是功德圆满的高香。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.6 如何拿香？是烧香再叩头。烧香的话，应该是左手拿香，右手拿烛，因为常人用右手杀鸡剖鱼，如果是左撇子的话，则反之。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.7 如何作揖？烧香时，先点燃香，要越旺越好，人们就常说香火旺盛嘛。左手在上，右手在下握住香，高举过头顶作揖。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.8 如何叩头？作揖后，把香插在香灰里，就可进门叩头了。叩头的话，要认准佛祖菩萨或罗汉。比如，你不能向地藏菩萨一阵猛叩头&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;4.9 如何跪拜？跪拜的姿势是这样的，双膝跪在蒲团上，双手合什，注意，这个双掌合什要注意手心处呈空心状，高举过头顶，向下至嘴边停顿，可许愿，再向下至心口，默念，再摊开双掌，掌心向上，上身拜倒。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;5 五台山烧头香的注意事项&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;5.1 五台山礼佛人数淡旺季非常不平衡，旅游旺季人山人海，可5-6点早一些前往，可以避开高峰。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;5.2 节假日，比如春节、五一、国庆节等黄金周期间要提前10天以上联系寺院安排。&lt;/p&gt;&lt;p style=&quot;; ; ;&quot;&gt;5.3 五台山为佛教圣地和世界遗产地，各寺院文物古迹遍布全山，因此，要树立防火意识，听从寺院的安排。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1 何谓烧头香烧头香分为绝对和相对的头香。相对头香是说，到五台山信众和游客，第一件事情往往是到寺院敬香，称之为烧头香；礼佛期间清晨只要您早早前去寺庙进香，也称之为头香。绝对头香就是大年初一全山燃起的...\r\n', '2013_12/max_52b9b2f35c805.jpg', '2013_12/medium_52b9b2f35c805.jpg', '2013_12/mini_52b9b2f35c805.jpg', 1, 1388062416),
(4, '忻州市忻府区董村镇遊邀', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/qingchun/uploads/20131225/52ba7a127ecc6.jpg&quot; title=&quot;525417bc2c20d.jpg&quot; width=&quot;400&quot; height=&quot;282&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 400px; height: 282px;&quot;/&gt;&lt;/p&gt;&lt;p&gt;忻州市忻府区董村镇遊邀（牛叫村）千佛寺济民堡古庙会暨诸佛菩萨安坐开光法会如期举行，参加此次活动的有忻府区宗教局王成和局长、忻州文史部门等相关领导，诸山长老及十里八乡的善信人士。 　　忻府区佛教协会会长照瑞大和尚、兴国寺方丈寂常老和尚拈香并主持开光仪式。 　　现场气氛热烈、法喜充满。&lt;br/&gt;&lt;/p&gt;', '忻州市忻府区董村镇遊邀（牛叫村）千佛寺济民堡古庙会暨诸佛菩萨安坐开光法会如期举行，参加此次活动的有忻府区宗教局王成和局长、忻州文史部门等相关领导，诸山长老及十里八乡的善信人士。 　　忻府区佛教协会会长照瑞大和尚、兴国寺方丈寂常老和尚拈香并主持开光仪式。 　　现场气氛热烈、法喜充满。', '2013_12/max_52ba788b2a1f6.jpg', '2013_12/medium_52ba788b2a1f6.jpg', '2013_12/mini_52ba788b2a1f6.jpg', 1, 1387952704),
(5, '千佛寺古庙会菩萨安坐开光法会', '&lt;h1 class=&quot;ue_t&quot; label=&quot;Title center&quot; name=&quot;tc&quot; style=&quot;border-bottom-color:#cccccc;border-bottom-width:2px;border-bottom-style:solid;padding:0px 4px 0px 0px;text-align:center;margin:0px 0px 20px;&quot;&gt;&lt;span style=&quot;color:#c0504d;&quot;&gt;[键入文档标题]&lt;/span&gt;&lt;/h1&gt;&lt;p style=&quot;text-align:center;&quot;&gt;&lt;strong class=&quot;ue_t&quot;&gt;[键入文档副标题]&lt;/strong&gt;&lt;/p&gt;&lt;h3&gt;&lt;span class=&quot;ue_t&quot; style=&quot;font-family:幼圆&quot;&gt;[标题 1]&lt;/span&gt;&lt;/h3&gt;&lt;p class=&quot;ue_t&quot; style=&quot;text-indent:2em;&quot;&gt;对于“插入”选项卡上的库，在设计时都充分考虑了其中的项与文档整体外观的协调性。 您可以使用这些库来插入表格、页眉、页脚、列表、封面以及其他文档构建基块。 您创建的图片、图表或关系图也将与当前的文档外观协调一致。&lt;/p&gt;&lt;h3&gt;&lt;span class=&quot;ue_t&quot; style=&quot;font-family:幼圆&quot;&gt;[标题 2]&lt;/span&gt;&lt;/h3&gt;_ueditor_page_break_tag_&lt;p class=&quot;ue_t&quot; style=&quot;text-indent:2em;&quot;&gt;在“开始”选项卡上，通过从快速样式库中为所选文本选择一种外观，您可以方便地更改文档中所选文本的格式。 您还可以使用“开始”选项卡上的其他控件来直接设置文本格式。大多数控件都允许您选择是使用当前主题外观，还是使用某种直接指定的格式。&lt;/p&gt;&lt;p class=&quot;ue_t&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会', '2013_12/max_52b9b2f35c805.jpg', '2013_12/medium_52b9b2f35c805.jpg', '2013_12/mini_52b9b2f35c805.jpg', 1, 1387904570),
(6, '忻州市忻府区董村镇遊邀', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/qingchun/uploads/20131225/52ba7a127ecc6.jpg&quot; title=&quot;525417bc2c20d.jpg&quot; width=&quot;400&quot; height=&quot;282&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width: 400px; height: 282px;&quot;/&gt;&lt;/p&gt;&lt;p&gt;忻州市忻府区董村镇遊邀（牛叫村）千佛寺济民堡古庙会暨诸佛菩萨安坐开光法会如期举行，参加此次活动的有忻府区宗教局王成和局长、忻州文史部门等相关领导，诸山长老及十里八乡的善信人士。 　　忻府区佛教协会会长照瑞大和尚、兴国寺方丈寂常老和尚拈香并主持开光仪式。 　　现场气氛热烈、法喜充满。&lt;br/&gt;&lt;/p&gt;', '忻州市忻府区董村镇遊邀（牛叫村）千佛寺济民堡古庙会暨诸佛菩萨安坐开光法会如期举行，参加此次活动的有忻府区宗教局王成和局长、忻州文史部门等相关领导，诸山长老及十里八乡的善信人士。 　　忻府区佛教协会会长照瑞大和尚、兴国寺方丈寂常老和尚拈香并主持开光仪式。 　　现场气氛热烈、法喜充满。', '2013_12/max_52ba788b2a1f6.jpg', '2013_12/medium_52ba788b2a1f6.jpg', '2013_12/mini_52ba788b2a1f6.jpg', 1, 1387952704),
(7, '千佛寺古庙会菩萨安坐开光法会', '&lt;h1 class=&quot;ue_t&quot; label=&quot;Title center&quot; name=&quot;tc&quot; style=&quot;border-bottom-color:#cccccc;border-bottom-width:2px;border-bottom-style:solid;padding:0px 4px 0px 0px;text-align:center;margin:0px 0px 20px;&quot;&gt;&lt;span style=&quot;color:#c0504d;&quot;&gt;[键入文档标题]&lt;/span&gt;&lt;/h1&gt;&lt;p style=&quot;text-align:center;&quot;&gt;&lt;strong class=&quot;ue_t&quot;&gt;[键入文档副标题]&lt;/strong&gt;&lt;/p&gt;&lt;h3&gt;&lt;span class=&quot;ue_t&quot; style=&quot;font-family:幼圆&quot;&gt;[标题 1]&lt;/span&gt;&lt;/h3&gt;&lt;p class=&quot;ue_t&quot; style=&quot;text-indent:2em;&quot;&gt;对于“插入”选项卡上的库，在设计时都充分考虑了其中的项与文档整体外观的协调性。 您可以使用这些库来插入表格、页眉、页脚、列表、封面以及其他文档构建基块。 您创建的图片、图表或关系图也将与当前的文档外观协调一致。&lt;/p&gt;&lt;h3&gt;&lt;span class=&quot;ue_t&quot; style=&quot;font-family:幼圆&quot;&gt;[标题 2]&lt;/span&gt;&lt;/h3&gt;_ueditor_page_break_tag_&lt;p class=&quot;ue_t&quot; style=&quot;text-indent:2em;&quot;&gt;在“开始”选项卡上，通过从快速样式库中为所选文本选择一种外观，您可以方便地更改文档中所选文本的格式。 您还可以使用“开始”选项卡上的其他控件来直接设置文本格式。大多数控件都允许您选择是使用当前主题外观，还是使用某种直接指定的格式。&lt;/p&gt;&lt;p class=&quot;ue_t&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会遊邀千佛寺古庙会暨诸佛菩萨安坐开光法会', '2013_12/max_52b9b2f35c805.jpg', '2013_12/medium_52b9b2f35c805.jpg', '2013_12/mini_52b9b2f35c805.jpg', 1, 1387904570),
