--
-- 表的结构 `qc_carhire`
--

CREATE TABLE IF NOT EXISTS `qc_carhire` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '编号',
  `city` varchar(45) NOT NULL DEFAULT '' COMMENT '城市',
  `models` varchar(45) NOT NULL DEFAULT '' COMMENT '车型',
  `pricerange` varchar(45) NOT NULL DEFAULT '' COMMENT '价格区间',
  `price` varchar(45) NOT NULL DEFAULT '' COMMENT '价格',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `phone` varchar(30) NOT NULL DEFAULT '15135032660',
  `content` text NOT NULL COMMENT '内容',
  `max` varchar(70) NOT NULL,
  `medium` varchar(70) NOT NULL,
  `mini` varchar(70) NOT NULL,
  `remed` int(1) NOT NULL DEFAULT '1' COMMENT '推荐首页1：推荐0：不推荐',
  `addtime` int(30) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='旅游租车' AUTO_INCREMENT=5 ;

