--
-- 表的结构 `qc_category`
--

CREATE TABLE IF NOT EXISTS `qc_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '编号',
  `fenlei` varchar(100) NOT NULL DEFAULT '' COMMENT '分类',
  `title` char(80) NOT NULL COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `about` varchar(100) NOT NULL COMMENT '简介',
  `addtime` int(20) NOT NULL COMMENT '发表时间',
  `max` varchar(70) NOT NULL COMMENT '大图',
  `medium` varchar(70) NOT NULL COMMENT '中图',
  `mini` varchar(70) NOT NULL COMMENT '小图',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='会议分类' AUTO_INCREMENT=10 ;

--
-- 转存表中的数据 `qc_category`
--

INSERT INTO `qc_category` (`id`, `fenlei`, `title`, `content`, `about`, `addtime`, `max`, `medium`, `mini`) VALUES
(1, '会议酒店', '山西国贸大饭店', '山西国贸大饭店是依托山西国际贸易中心的一流品质建造的涉外旅游饭店，具有豪华的装修、齐全的设施、完善的功能。委托上海锦江国际酒店管理公司管理，可为宾客提供全方位、高品质的服务', '山西国贸大饭店是依托山西国际贸易中心的一流品质建造的涉外旅游饭店，具有豪华的装修、齐全的设施、完善的功能。委托上海锦江国际酒店管理公司管理，可为宾客提供全方位、高品质的服务。\r\n', 1386253293, '2013_12/max_52a07e0f06c3a.jpg', '2013_12/medium_52a07e0f06c3a.jpg', '2013_12/mini_52a07e0f06c3a.jpg'),
(2, '会议策划', '签到接待', '会场一般要签到，会议签到是为了及时、准确地统计到会人数，便于安排会议工作。有些会议只有达到一定人数才能召开，否则会议通过的决议无效。因此，会议签到是一项重要的会前工作。它是出席也是会中任务的重要内容之一。会议签到一般以下几种方法：与会人员在会议工作人员预先备好的签到簿上按要求签置自己的姓名，表示到会。签到簿上的内容一般有姓名、职务、所代表的单位等，与会人员必须逐项填写，不得遗漏。簿式签到的优点是利于保存，便于查找。缺点是这种方法只适用于小型会议，一些大型会议，参加会议的人数很多，采用簿式签到就不太方便。2.证卡签到会议工作员将印好的签到证事先发给每们与会人员，签证卡上一般印有会议的名称、日期、座次号、编号等，与会人员在签证卡上写好自己的姓名，进入会场时，将签证瞳交给会议工作人员，表示到会。其优点是比较方便，避免临开会时签到造成拥挤。缺点是不便保存查找。证卡签到多用于大中型会议。3.会议工作人员代为签到会议工作人员事先制定好参加本次会议的花名册，开会时，来一人就在该人名单后画上记号，表示到会，缺席和请假人员也要用规定的记号表示。例如：“√”表示到会，用“×”表示缺席，用“0”表示请假等。这种会议签到方法比较简便易行，但要求会议工作人员必须认识绝大部分与会人员，所以这种方法只适宜于小型会议和一些常规性会议。对于一些大型会议，与会人员很多，会议工作人员不能认识大部分人，逐个询问到会人员的姓名很麻烦，所以大型会议不适宜采用这种方法4.座次表签到方法会议工作人员按照会议模型，事先制定好座次表，座次表上每个座位按要求填上合适的与会人员姓名和座位号码。参加会议的人员到会时，就在座次表上消号，表示出席。印制座次表，与会人员座次安排要求有一定规律，如从×号到号是某部门代表座位，将同一部门的与会人员集中一起，便于与会者查找自己的座次号。采用座次表签到，参加会议的人员在签到时就知道了自己的座次号。采用座次表签到，参加会议的人员在签到时就知道了自己座位的排数和座号，起到引导的效果。5.电脑签到电脑签到快速、准确、简便，参加会议的人员进入会场时，只要把特制的卡片放到签到机内，签到机就将与会人员的姓名、号码传到中心，与会者的签到手续就在几秒钟即办完，将签到卡退还本人，参加会议人员到会结果由计算机准确、迅速地显示出来。电脑签到是先进的签到手段，一些大型会议都是采用电脑签到。', '参加会议人员在进入会场一般要签到，会议签到是为了及时、准确地统计到会人数，便于安排会议工作。有些会议只有达到一定人数才能召开，否则会议通过的决议无效。因此，会议签到是一项重要的会前工作。它是出席也是会', 1386254895, '', '', ''),
(3, '会议酒店', '山西国贸大饭店', '山西国贸大饭店是依托山西国际贸易中心的一流品质建造的涉外旅游饭店，具有豪华的装修、齐全的设施、完善的功能。委托上海锦江国际酒店管理公司管理，可为宾客提供全方位、高品质的服务', '山西国贸大饭店是依托山西国际贸易中心的一流品质建造的涉外旅游饭店，具有豪华的装修、齐全的设施、完善的功能。委托上海锦江国际酒店管理公司管理，可为宾客提供全方位、高品质的服务。\r\n', 1386253293, '2013_12/max_52a07e0f06c3a.jpg', '2013_12/medium_52a07e0f06c3a.jpg', '2013_12/mini_52a07e0f06c3a.jpg'),
(4, '会议酒店', '山西国贸大饭店', '山西国贸大饭店是依托山西国际贸易中心的一流品质建造的涉外旅游饭店，具有豪华的装修、齐全的设施、完善的功能。委托上海锦江国际酒店管理公司管理，可为宾客提供全方位、高品质的服务', '山西国贸大饭店是依托山西国际贸易中心的一流品质建造的涉外旅游饭店，具有豪华的装修、齐全的设施、完善的功能。委托上海锦江国际酒店管理公司管理，可为宾客提供全方位、高品质的服务。\r\n', 1386253293, '2013_12/max_52a07e0f06c3a.jpg', '2013_12/medium_52a07e0f06c3a.jpg', '2013_12/mini_52a07e0f06c3a.jpg'),
