--
-- 表的结构 `qc_circuit`
--

CREATE TABLE IF NOT EXISTS `qc_circuit` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '编号',
  `region` varchar(100) NOT NULL DEFAULT '' COMMENT '区域',
  `city` varchar(100) NOT NULL DEFAULT '山西忻州' COMMENT '出发城市',
  `transportation` varchar(100) NOT NULL DEFAULT '' COMMENT '交通方式',
  `tourtime` int(11) NOT NULL DEFAULT '0' COMMENT '行程天数',
  `telme` varchar(30) NOT NULL DEFAULT '15135032660' COMMENT '联系电话',
  `title` varchar(100) NOT NULL DEFAULT '' COMMENT '标题',
  `about` varchar(100) NOT NULL DEFAULT '''''' COMMENT '简介',
  `content` text NOT NULL COMMENT '内容',
  `addtime` int(20) NOT NULL COMMENT '发布时间',
  `max` varchar(70) NOT NULL,
  `medium` varchar(70) NOT NULL,
  `mini` varchar(70) NOT NULL,
  `remed` int(1) NOT NULL DEFAULT '1' COMMENT '推荐首页1：推荐0：不推荐',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='线路管理' AUTO_INCREMENT=17 ;

--
-- 转存表中的数据 `qc_circuit`
--

INSERT INTO `qc_circuit` (`id`, `region`, `city`, `transportation`, `tourtime`, `telme`, `title`, `about`, `content`, `addtime`, `max`, `medium`, `mini`, `remed`) VALUES
(13, '华东地区路线', '山西忻州', '空调旅游大巴', 4, '15135032660', '【阎锡山故居四日游】（山西忻州定襄）', '阎锡山故居，位于山西省定襄县城东北22公里的河边村。始建于二十年代初，三十年代结束，长达14年之久，耗资140万两银。整个建筑群坐东向西，原长135.2米，宽119.7米，占地面积61834平方米，近', '&lt;p&gt;&lt;span style=&quot;color: rgb(68, 68, 68); font-family: SimSun, 宋体, &amp;#39;Arial Narrow&amp;#39;; font-size: 13px; &quot;&gt;【阎锡山故居二日游】（山西忻州定襄）&lt;/span&gt;&lt;/p&gt;', 1388324748, '', '', '', 1),
(14, '华北地区路线', '山西忻州', '空调旅游大巴', 5, '15135032660', '【阎锡山故居五日游】（山西忻州定襄）', '阎锡山故居，位于山西省定襄县城东北22公里的河边村。始建于二十年代初，三十年代结束，长达14年之久，耗资140万两银。整个建筑群坐东向西，原长135.2米，宽119.7米，占地面积61834平方米，近', '&lt;p&gt;&lt;span style=&quot; color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;阎锡山故居，位于&lt;/span&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/18758.htm&quot; style=&quot;white-space: normal; text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;山西省&lt;/a&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/644767.htm&quot; style=&quot;white-space: normal; text-decoration: none; color: rgb(19, 110, 194); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;定襄县&lt;/a&gt;&lt;span style=&quot; color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;城东北22公里的河边村。始建于二十年代初，三十年代结束，长达14年之久，耗资140万两银。整个建筑群坐东向西，原长135.2米，宽119.7米，占地面积61834平方米，近30座院落，八百余间房屋。现存18座院落575间房层，占地面积38000平方米，建筑面积18000平方米。总体设计分为上下两院，前后为东西花园。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;span style=&quot; color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;http://127.0.0.1/qingchun/Data/Ueditor/php/upload/23961388324849.jpg&quot; width=&quot;350&quot; height=&quot;465&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; title=&quot;&quot; style=&quot;width: 350px; height: 465px;&quot;/&gt;&lt;/span&gt;&lt;/p&gt;', 1388324926, '', '', '', 1),
(15, '东北地区路线', '山西忻州', '空调旅游大巴', 6, '15135032660', '【阎锡山故居六日游】（山西忻州定襄）', '阎锡山故居，位于山西省定襄县城东北22公里的河边村。始建于二十年代初，三十年代结束，长达14年之久，耗资140万两银。整个建筑群坐东向西，原长135.2米，宽119.7米，占地面积61834平方米，近', '&lt;p&gt;&lt;span style=&quot;color: rgb(68, 68, 68); font-family: SimSun, 宋体, &amp;#39;Arial Narrow&amp;#39;; font-size: 13px; &quot;&gt;【阎锡山故居一日游】（山西忻州定襄）&lt;/span&gt;&lt;/p&gt;', 1388324956, '', '', '', 1),
(11, '华中地区路线', '山西忻州', '空调旅游大巴', 2, '15135032660', '【阎锡山故居一日游】（山西忻州定襄）', '阎锡山故居，位于山西省定襄县城东北22公里的河边村。始建于二十年代初，三十年代结束，长达14年之久，耗资140万两银。整个建筑群坐东向西，原长135.2米，宽119.7米，占地面积61834平方米，近', '&lt;p&gt;&lt;span style=&quot;color: rgb(68, 68, 68); font-family: SimSun, 宋体, &amp;#39;Arial Narrow&amp;#39;; font-size: 13px; &quot;&gt;【阎锡山故居二日游】（山西忻州定襄）&lt;/span&gt;&lt;/p&gt;', 1388324729, '', '', '', 1),
(12, '华南地区路线', '山西忻州', '空调旅游大巴', 3, '15135032660', '【阎锡山故居三日游】（山西忻州定襄）', '阎锡山故居，位于山西省定襄县城东北22公里的河边村。始建于二十年代初，三十年代结束，长达14年之久，耗资140万两银。整个建筑群坐东向西，原长135.2米，宽119.7米，占地面积61834平方米，近', '&lt;p&gt;&lt;span style=&quot;color: rgb(68, 68, 68); font-family: SimSun, 宋体, &amp;#39;Arial Narrow&amp;#39;; font-size: 13px; &quot;&gt;【阎锡山故居二日游】（山西忻州定襄）&lt;/span&gt;&lt;/p&gt;', 1388324737, '', '', '', 1),
(8, '港/澳/台路线', '山西忻州', '汽车大巴', 3, '15135032660', '【五台山三日游】', '五台山（Mount Wutai）位于山西省忻州市五台县境内，位列中国佛教四大名山之首。五台山位于山西省东北部，隶属忻州市五台县，西南距省会太原市230公里，与浙江普陀山、安徽九华山、四川峨眉山、共称“', '&lt;p&gt;五台山（Mount Wutai）位于&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/6204.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;山西&lt;/a&gt;省忻州市&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/644008.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;五台县&lt;/a&gt;境内，位列中国&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/4696.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;佛教&lt;/a&gt;四大名山之首。五台山位于&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/18758.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;山西省&lt;/a&gt;东北部，隶属&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/20800.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;忻州&lt;/a&gt;市&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/644008.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;五台县&lt;/a&gt;，西南距省会&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/23736.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;太原市&lt;/a&gt;230公里，与浙江&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/22247.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;普陀山&lt;/a&gt;、安徽&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/7270.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;九华山&lt;/a&gt;、四川&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/2167.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;峨眉山&lt;/a&gt;、共称“&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/125695.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;中国佛教四大名山&lt;/a&gt;”。与尼泊尔&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/87548.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;蓝毗尼&lt;/a&gt;花园、印度&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/83347.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;鹿野苑&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/163604.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;菩提伽耶&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/126234.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;拘尸那迦&lt;/a&gt;并称为世界五大佛教圣地。&lt;/p&gt;&lt;p&gt;五台山所在的山西处于&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/6776.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;黄土高原&lt;/a&gt;，地旱树稀，视野里整整一个是土黄色的世界，可以称为金色世界。五台山属太行山系的北端，跨忻州市五台县、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/65003.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;繁峙&lt;/a&gt;县、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/65022.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;代县&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/64984.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;原平&lt;/a&gt;市、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/644767.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;定襄县&lt;/a&gt;，周五百余里。在北纬38°50&amp;#39;～39°05&amp;#39;、东经113°29&amp;#39;～113°44&amp;#39;之间，由一系列大山和群峰组成。其中五座高峰，山势雄伟，连绵环抱，方圆达250公里，总面积592.88平方公里。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1388325366, '2013_12/max_52b92cf68c698.gif', '2013_12/medium_52b92cf68c698.gif', '2013_12/mini_52b92cf68c698.gif', 1),
(9, '华中地区路线', '山西忻州', '空调旅游大巴', 1, '15135032660', '【芦芽山一日游】忻州市宁武', '芦芽山系管涔山的主峰，位于山西宁武、五寨、岢岚县境内，五寨县城东南26公里处，属吕梁山脉，“西接岢岚，东带宁武，南连静乐而雄踞五寨”。因形似一“芦芽”而得名，海拔2739米，恰似一尊巨人手擎利剑直插云', '&lt;p&gt;芦芽山系&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/65959.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;管涔山&lt;/a&gt;的&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/766314.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;主峰&lt;/a&gt;，芦芽山在宁武县城西北70公里的管涔山腹地，群山森列，重峦&lt;/p&gt;&lt;p&gt;&lt;a hidefocus=&quot;true&quot; class=&quot;nslog:1199&quot; href=&quot;http://baike.baidu.com/picview/65796/65796/0/3792cb39bad900e03b87ce49.html&quot; target=&quot;_blank&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194); height: 140px; width: 100px; display: block; position: relative;&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;叠嶂，危崖峭壁，碧波松涛，其主峰更是峰峦特秀，怪石嶙峋，巍峨挺拔，雄距朔方。这便是国家级优秀&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/68284.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;森林公园&lt;/a&gt;、闻名晋西北的古毗卢佛道场面积21453公顷，1980年经山西省人民政府批准建立，1997年晋升为国家级，主要保护对象为珍禽褐马鸡及森林生态系统。&lt;span style=&quot; margin-left: 2px; color: rgb(51, 102, 204); cursor: pointer; padding: 0px 2px; position: relative; vertical-align: baseline; top: -0.5em;&quot;&gt;[1]&lt;/span&gt;&lt;a style=&quot;color: rgb(19, 110, 194);&quot; name=&quot;ref_[1]_65796&quot;&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;本区地处吕梁山系的北端，主峰芦芽山海拔2736米。区内山系奇特险峻，生态环境复杂，保存有大面积华北落叶林和大片的云杉林，是目前黄土高原森林生态系统保存最完好的地区之一。这里&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/1104324.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;植物区系成分&lt;/a&gt;复杂，除华北成分外，还有&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/19558.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;蒙古&lt;/a&gt;成分、东北成分和古北成分，并含有华北特有种。高等植物有93科、370属、700多钟，已发现的脊椎动物有232种，其中列为国家一级保护的野生动物有褐马鸡、黑鹤、金雕、金钱豹等6种，二级保护野生动物有原麝、猞猁、古貂、马鹿、苍鹰、红隼等22种。特别是世界性濒危珍稀动物褐马鸡在保护区内有较大数量的分布，种群数量达2700只左右，并为褐马鸡分布的最北限。&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/246880.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;芦芽山自然保护区&lt;/a&gt;的建立，对保护和研究褐马鸡具有重大的意义。&lt;/p&gt;&lt;p&gt;芦芽山巅，有&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/1130938.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;金龙池&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/264888.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;五龙洞&lt;/a&gt;、秀云峰、紫峰崖、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/2152.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;凤凰岭&lt;/a&gt;、东西火场、大小天涧、束身峡、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/488689.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;舍身崖&lt;/a&gt;、九桄梯、华严守、说法台、金刚佛堂、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/550648.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;龙王堂&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/168469.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;太子殿&lt;/a&gt;等名胜。太子殿&lt;/p&gt;&lt;p&gt;&amp;lt;span class=&amp;quot;BMap_Marker BMap_noprint&amp;quot; unselectable=&amp;quot;on&amp;quot; &amp;quot;=&amp;quot;&amp;quot; title=&amp;quot;&amp;quot; style=&amp;quot;position: absolute; padding: 0px; margin: 0px; border: 0px; cursor: pointer; background-image: url(http://api0.map.bdimg.com/images/blank.gif); width: 19px; height: 25px; left: 145px; top: 88px; z-index: -7749106; background-position: initial initial; background-repeat: initial initial;&amp;quot;&amp;gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;BMap_Marker&quot; unselectable=&quot;on&quot; style=&quot;position: absolute; padding: 0px; margin: 0px; border: 0px; width: 0px; height: 0px; left: 145px; top: 88px; z-index: -7749106;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span unselectable=&quot;on&quot; style=&quot;position: absolute; padding: 0px; margin: 0px; border: 0px; width: 20px; height: 11px; left: 149px; top: 102px;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;http://online4.map.bdimg.com/tile/?qt=tile&amp;amp;x=189&amp;amp;y=70&amp;amp;z=10&amp;amp;styles=pl&amp;amp;udt=20131219&quot; style=&quot;border: none; position: absolute; width: 256px; height: 256px; left: -292px; top: 21px; max-width: none; opacity: 1;&quot;/&gt;&lt;img src=&quot;http://online1.map.bdimg.com/tile/?qt=tile&amp;amp;x=190&amp;amp;y=71&amp;amp;z=10&amp;amp;styles=pl&amp;amp;udt=20131219&quot; style=&quot;border: none; position: absolute; width: 256px; height: 256px; left: -36px; top: -235px; max-width: none; opacity: 1;&quot;/&gt;&lt;img src=&quot;http://online0.map.bdimg.com/tile/?qt=tile&amp;amp;x=189&amp;amp;y=71&amp;amp;z=10&amp;amp;styles=pl&amp;amp;udt=20131219&quot; style=&quot;border: none; position: absolute; width: 256px; height: 256px; left: -292px; top: -235px; max-width: none; opacity: 1;&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span _cid=&quot;1&quot; style=&quot;display: inline;&quot;&gt;&lt;span style=&quot;font-size: 11px;&quot;&gt;© 2013 Baidu&amp;nbsp;- Data ©&amp;nbsp;&lt;a target=&quot;_blank&quot; href=&quot;http://www.navinfo.com/&quot; style=&quot;text-decoration: none; color: rgb(121, 121, 204); font-size: 11px; display: inline;&quot;&gt;NavInfo&lt;/a&gt;&amp;nbsp;&amp;amp;&amp;nbsp;&lt;a target=&quot;_blank&quot; href=&quot;http://www.cennavi.com.cn/&quot; style=&quot;text-decoration: none; color: rgb(121, 121, 204); font-size: 11px; display: inline;&quot;&gt;CenNavi&lt;/a&gt;&amp;nbsp;&amp;amp;&amp;nbsp;&lt;a target=&quot;_blank&quot; href=&quot;http://www.365ditu.com/&quot; style=&quot;text-decoration: none; color: rgb(121, 121, 204); font-size: 11px; display: inline;&quot;&gt;道道通&lt;/a&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;title&quot; style=&quot;font-size: 12px;  line-height: 16px; margin: 2px 0px 0px; color: rgb(0, 0, 0); display: block;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;东又有小芦芽山、云际寺、饿虎垴。小芦芽山背后又有&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/895372.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;达摩庵&lt;/a&gt;、石栈天桥、大河畔等自然景观。&lt;/p&gt;&lt;p&gt;芦芽山区，森林密布，泉水四溢，遍地野生药材，珍奇动物出没林间，野花飘香，芳草丛生。&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/58460.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;汾河&lt;/a&gt;、清涟河、岚骑河都源于此。每年春耕结束，便有&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/64963.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;五寨&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/64958.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;神池&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/52702.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;宁武&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/64991.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;静乐&lt;/a&gt;、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/20680.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;岢岚&lt;/a&gt;等县的农民，赶着成千上万只牛驴骡马来此放牧。&lt;/p&gt;&lt;p&gt;芦芽山&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/6634719.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;极顶&lt;/a&gt;的“太子殿”，有副门联：“霞慈雨于中回，性法雨于边方。”传说，&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/1690.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;朱元璋&lt;/a&gt;的第十八子因未加封，一气之下远离京城，削发为僧，在&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/575919.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;晋西北&lt;/a&gt;说法讲经，劝世人以农为本，便可丰衣足食。世人为纪念这位太子的高功厚德，便选在芦芽山尖盖了座太子殿。关于“太子殿”的来历，当地还有另一种说法，此庙是为&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/2532774.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;佛祖释迦牟尼&lt;/a&gt;的太子&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/1075552.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;罗侯罗&lt;/a&gt;修建。芦芽山一带，明清时有大小寺庙72座。过去遇有天旱不雨，便有乡民到芦芽山顶烧香摆供，还愿祈雨。&lt;/p&gt;&lt;p&gt;芦芽山的原始生物保护得很好。据&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/29296.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;中国农业科学院&lt;/a&gt;专家&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/196524.htm&quot; style=&quot;text-decoration: none; color: rgb(19, 110, 194);&quot;&gt;鲍文奎&lt;/a&gt;说：“芦芽山自然景区是世界上少有的原始生物基因宝库。”&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 1388325185, '2013_12/max_52b92a0522b5c.gif', '2013_12/medium_52b92a0522b5c.gif', '2013_12/mini_52b92a0522b5c.gif', 1),
(10, '华中地区路线', '山西忻州', '空调旅游大巴', 2, '15135032660', '【阎锡山故居二日游】（山西忻州定襄）', '五台山（Mount Wutai）位于山西省忻州市五台县境内，位列中国佛教四大名山之首。五台山位于山西省东北部，隶属忻州市五台县，西南距省会太原市230公里，与浙江普陀山、安徽九华山、四川峨眉山、共称“', '&lt;p&gt;【阎锡山故居一日游】（山西忻州定襄）&lt;/p&gt;', 1388325427, '2013_12/max_52b92c42b68d6.gif', '2013_12/medium_52b92c42b68d6.gif', '2013_12/mini_52b92c42b68d6.gif', 1),
(7, '华东地区路线', '山西忻州', '空调旅游大巴', 1, '15135032660', '【上海都市一日游】登东方明珠、豪华船游黄浦江、浦江观光隧道、外滩、城隍庙（上海市内旅游）', '含人费用包：\r\n1、交通：空调旅游大巴往返 [散客天天发；上海市内免费到住地接]\r\n2、门票：包含大小景点门票.\r\n3、导游：全程金牌导游一流讲解服务\r\n4、保险：我社已投保旅行社责任险（游客人身意外', '&lt;p&gt;&lt;img style=&quot;width: 600px; height: 432px;&quot; title=&quot;2013881882371712.jpg&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; src=&quot;/qingchun/uploads/20131205/52a0410f502cd.jpg&quot; width=&quot;600&quot; height=&quot;432&quot;/&gt;&lt;/p&gt;&lt;p&gt;行程介绍&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 16px;&quot;&gt;&lt;span style=&quot;color: rgb(227, 108, 9); font-size: 14px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 14px;&quot;&gt;&lt;/span&gt;【上海市内免费接，散客上车以后付费】&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;span style=&quot;color: rgb(247, 150, 70);&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;span style=&quot;color: rgb(255, 102, 0);&quot;&gt;&lt;/span&gt;&lt;/span&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp;&lt;span style=&quot;font-size: 12px;&quot;&gt;&lt;span style=&quot;color: rgb(255, 102, 0); font-size: 16px;&quot;&gt;&lt;span style=&quot;color: rgb(255, 102, 0); font-size: 18px;&quot;&gt;早08：00点人民广场准时发车，登&lt;/span&gt;&lt;/span&gt;&lt;strong style=&quot;text-align: justify; color: rgb(255, 102, 0); text-transform: none; line-height: normal; text-indent: 0px; letter-spacing: normal; font-family: 宋体; font-size: small; font-style: normal; font-variant: normal; word-spacing: 0px; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;span style=&quot;color: rgb(51, 204, 82); font-size: 16px;&quot;&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;【东方明珠电视塔】-&lt;/span&gt;&lt;span style=&quot;color: rgb(227, 108, 9); font-size: 18px;&quot;&gt;（&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 102, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; float: none; display: inline !important; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;登到第二个球/最高球是旋转餐厅）263米观光层及259米悬空观光长廊（可透过脚下的透明玻璃，俯览黄埔江两岸全景，感受在云中漫步的感觉）、远眺上海全城美景、俯览上海全城建筑（约120分钟）。参观&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; float: none; display: inline !important; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;【&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font: small/normal 宋体; text-align: justify; color: rgb(60, 196, 87); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;span style=&quot;font: 20px/normal 宋体; text-align: justify; color: rgb(255, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;百年历史陈列馆&lt;/span&gt;】—&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 102, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; float: none; display: inline !important; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;2000多米长的展示区&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; float: none; display: inline !important; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;-&lt;/span&gt;&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; float: none; display: inline !important; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;/span&gt;栩栩如生的蜡像、模型、灯光、声效等带您进入&lt;/span&gt;&lt;strong style=&quot;text-align: justify; color: rgb(255, 102, 0); text-transform: none; line-height: normal; text-indent: 0px; letter-spacing: normal; font-family: 宋体; font-size: small; font-style: normal; font-variant: normal; word-spacing: 0px; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;【时空隧&lt;/span&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;道】.&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;font: 18px/normal 宋体; text-align: justify; color: rgb(255, 102, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; float: none; display: inline !important; orphans: 2; widows: 2; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;深切了解上海百年沧桑(约1小时30分)。乘坐-&lt;/span&gt;&lt;strong style=&quot;text-align: justify; color: rgb(255, 102, 0); text-transform: none; line-height: normal; text-indent: 0px; letter-spacing: normal; font-family: 宋体; font-size: small; font-style: normal; font-variant: normal; word-spacing: 0px; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;【豪华邮轮游玩&lt;strong&gt;&lt;span style=&quot;color: rgb(227, 108, 9);&quot;&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;黄浦江】-&lt;/span&gt;&lt;span style=&quot;color: rgb(247, 150, 70); font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;color: rgb(227, 108, 9);&quot;&gt;&lt;span style=&quot;color: rgb(247, 150, 70); font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;&lt;span style=&quot;color: rgb(227, 108, 9);&quot;&gt;&lt;span style=&quot;color: rgb(227, 108, 9); font-size: 16px;&quot;&gt;滔滔的黄浦江不仅是上&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;color: rgb(255, 0, 0); font-size: 18px;&quot;&gt;&lt;span style=&quot;color: rgb(227, 108, 9);&quot;&gt;&lt;span style=&quot;color: rgb(227, 108, 9); fon