CREATE TABLE IF NOT EXISTS `qc_hotel` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '编号',
  `grade` varchar(100) NOT NULL DEFAULT '' COMMENT '档次',
  `fanwei` varchar(100) NOT NULL COMMENT '价格范围',
  `price` float NOT NULL DEFAULT '0' COMMENT '价格',
  `location` varchar(45) NOT NULL DEFAULT '' COMMENT '地域',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `content` text NOT NULL COMMENT '内容',
  `addtime` int(11) NOT NULL COMMENT '发布时间',
  `remed` int(1) NOT NULL DEFAULT '1',
  `phone` varchar(30) NOT NULL DEFAULT '15135032660',
  `max` varchar(70) NOT NULL,
  `medium` varchar(70) NOT NULL,
  `mini` varchar(70) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='酒店管理' AUTO_INCREMENT=34 ;

