CREATE TABLE IF NOT EXISTS `qc_indexuser` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `password` char(32) NOT NULL COMMENT '密码',
  `userid` char(20) NOT NULL COMMENT '用户ID',
  `truename` varchar(100) NOT NULL DEFAULT '''''' COMMENT '真实姓名',
  `phone` varchar(30) NOT NULL DEFAULT '''''' COMMENT '手机号码',
  `QQ` varchar(15) NOT NULL DEFAULT '''''' COMMENT 'QQ号码',
  `sex` int(1) NOT NULL DEFAULT '1' COMMENT '1：男0：女',
  `email` varchar(100) NOT NULL DEFAULT '''''' COMMENT '邮箱',
  `location` varchar(200) NOT NULL DEFAULT '''''' COMMENT '收货地址',
  `loginip` varchar(100) NOT NULL DEFAULT '''''' COMMENT '登录IP',
  `logintime` varchar(100) NOT NULL DEFAULT '''''' COMMENT '登录时间',
  `registime` varchar(100) NOT NULL DEFAULT '''''' COMMENT '注册时间',
  `lock` int(2) NOT NULL DEFAULT '0' COMMENT '0：正常1：锁定',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=15 ;

